import React, { useEffect, useState } from 'react'
import { Card, CardContent, TextField, Typography, Button, Input } from '@material-ui/core'
import { Autocomplete } from '@material-ui/lab'
import { PhotoCamera } from '@material-ui/icons'
import Upload from 'react-images-uploading'
import useStyles from './styled'
import axios from 'axios'

const Postwisata = () => {

const [images, setImages] = React.useState([]);
  const maxNumber = 69;
  const onChange = (imageList, addUpdateIndex) => {
    // data for submit
    console.log(imageList[0].file.name);
    setImages(imageList);
  };

    const [kategori, setKategori] = useState([]);
    const [kategoriId, setKategoriId] = useState()
    const classes = useStyles();

    const handleUpload = () => {
        // const photo = document.querySelector("#photo").value;
        const wisata = document.querySelector("#wisata").value;
        const deskripsi = document.querySelector("#deskripsi").value;
        const lat = document.querySelector("#lat").value;
        const lang = document.querySelector("#lang").value;
        const url = document.querySelector("#url").value;

        const dataUpload  = {
            kategori_id: kategoriId,
            wisata: wisata,
            deskripsi: deskripsi,
            lat: lat,
            lang: lang,
            urlMap: url,
            gambar: images,
        }
        axios.post('http://localhost:5000/admin/upload', dataUpload)
        .then(res => {
            console.log(res)
        })
        .catch(err => {
            console.log(err)
        })
    }

    const handle = (e) => {
        console.log(e.name)
    }

    const getKategori = async () => {
        const kategoriData = await axios.get('http://localhost:5000/admin/getKategori');
        setKategori(kategoriData.data.data)
    }

    useEffect(() => {
        getKategori();
    }, [])

  return (
    <div className={classes.container}>
        <Card>
            <CardContent>
                <Typography>Upload Wisata</Typography>
                <div className={classes.areaUpload}>
                <Autocomplete
                    style={{margin: '20px 0'}}
                    options={kategori}
                    renderInput={params => (
                    <TextField {...params} label="Kategori" variant="outlined" />
                  )}
                  getOptionLabel={option => option.kategori}
                  onChange={(_event, kategoris) => {
                    setKategoriId(kategoris._id);
                  }}
                />
                <TextField style={{marginBottom: '20px'}} variant='outlined' id='wisata' label='Nama Wisata'/>
                <TextField style={{marginBottom: '20px'}} variant='outlined' id='deskripsi' label='Deskripsi'/>
                <TextField style={{marginBottom: '20px'}} variant='outlined' id='lat' label='Lat'/>
                <TextField style={{marginBottom: '20px'}} variant='outlined' id='lang' label='Lang'/>
                <TextField style={{marginBottom: '20px'}} variant='outlined' id='url' label='Url Map'/>
                <div>
                <Typography>Pilih Foto Wisata</Typography>
                <input onChange={handle} type='file'/>
                {/* <input onChange={handle} type='file'/>
                <label htmlFor="photo">
                <Input style={{display: 'none'}} accept="image/png" id="photo" multiple type="file" />
                <Button style={{marginBottom: '16px'}} variant="contained" component="span" color='primary' startIcon={<PhotoCamera/>}>
                    Change Photo
                </Button>
                </label> */}
                </div>
                <Upload
                multiple
                value={images}
                onChange={onChange}
                maxNumber={maxNumber}
                dataURLKey="data_url"
                >
                    {({
                        imageList,
                        onImageUpload,

                        isDragging,
                        dragProps
                        }) => (
                            <Button onClick={onImageUpload}
                            {...dragProps} style={{marginBottom: '16px'}} variant="contained" component="span" color='primary' startIcon={<PhotoCamera/>}>
                                Change Photo
                            </Button>
                            // <button
                            // style={isDragging ? { color: "red" } : null}
                            // onClick={onImageUpload}
                            // {...dragProps}
                            // >
                            // Click or Drop here
                            // </button>
                        )}
                </Upload>
                <Button onClick={handleUpload} variant='contained' color='primary'>Upload</Button>
                </div>
            </CardContent>
        </Card>
    </div>
  )
}

export default Postwisata
